CREATE TABLE public.aux_cargos (
    id_cargo SERIAL PRIMARY KEY,
    id_ambito integer NOT NULL,
    cargo character varying(20) NOT NULL,
    descripcion text,
    id_oficina integer NOT NULL,
    director boolean NOT NULL DEFAULT 't',
    id_usuario integer,
    id_suplente integer
);

COMMENT ON COLUMN public.aux_cargos.id_cargo IS 'corresponde al cargo';
COMMENT ON COLUMN public.aux_cargos.id_ambito IS 'corresponde al tipo de instalacion: 1=cg, 2=cr, 3=dl, 4=ctr';
COMMENT ON COLUMN public.aux_cargos.director IS 'director u oficial';
ALTER TABLE public.aux_cargos OWNER TO tramity;

CREATE TABLE public.aux_usuarios (
    id_usuario SERIAL PRIMARY KEY,
    usuario character varying(20) NOT NULL,
    id_cargo_preferido integer NOT NULL,
    password bytea,
    email text,
    nom_usuario text
);
COMMENT ON COLUMN public.aux_usuarios.id_cargo_preferido IS 'corresponde al cargo por defecto o preferido';
CREATE INDEX ON public.aux_usuarios ((lower(usuario)));
ALTER TABLE public.aux_usuarios OWNER TO tramity;

